import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class CurrencyConverterService {
  private apiUrl = 'http://localhost:5000/api/currencyconverter';

  constructor(private http: HttpClient) { }

  convertCurrency(amount: string): Observable<string> {
    return this.http.get<string>(`${this.apiUrl}/convert?amount=${amount}`);
  }
}
