import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-currency-converter',
  templateUrl: './currency-converter.component.html',
  styleUrls: ['./currency-converter.component.css']
})
export class CurrencyConverterComponent {
  amount: Number = 0;
  result = '';

  constructor(private http: HttpClient, @Inject('BASE_URL') private baseUrl: string) { }

  convertCurrency() {
    const url = `${this.baseUrl}currencyconvertervalues/${this.amount}`;
    this.http.get<Number>(url).subscribe(result => {
      this.amount = result;
      console.log(this.amount); // Added for debugging
      this.result = (Number(this.amount)).toString();
    }, error => console.error(error));
  }
}
<p>currency-converter works!</p>
<div>
  <input [(ngModel)]="amount" placeholder="Enter amount">
  <button (click)="convertCurrency()">Convert</button>
  <p>{{result}}</p>
</div>
