import { Component } from '@angular/core';
import { CurrencyConverterService } from '../../services/currency-converter.service';

@Component({
  selector: 'app-currency-converter',
  templateUrl: './currency-converter.component.html',
  styleUrls: ['./currency-converter.component.css']
})
export class CurrencyConverterComponent {
  amount: Number = 0;
  result = '';

  constructor(private currencyConverterService: CurrencyConverterService) { }

  convertCurrency() {
    this.currencyConverterService.convertCurrency(this.amount).subscribe(result => {
      console.log(result); // Added for debugging
      this.result = result.currencyText;
    }, error => console.error(error));
  }

  clearInput() {
    this.amount = 0;
    this.result = '';
  }
}

<div class="card bg-light" style="width: 24rem;">
  <div class="card-body">
    <p>currency-converter works!</p>
    <div>
      <input type="text" [(ngModel)]="amount" pattern="^\d{1,3}( \d{3})*,?\d{0,2}$" placeholder="Enter amount" class="form-control mb-3 mr-3">
      <button (click)="convertCurrency()" class="btn btn-primary mb-3 mr-3">Convert</button>
      <button (click)="clearInput()" class="btn btn-primary mb-3" style="margin-left: 5px;">Clear</button> <!-- Reduce margin-left here -->
      <p class="result text-wrap bg-white p-3" style="overflow-wrap: break-word;">{{result}}</p>
    </div>
  </div>
</div>
