import { Component } from '@angular/core';
import { CurrencyConverterService } from '../../services/currency-converter.service';

@Component({
  selector: 'app-currency-converter',
  templateUrl: './currency-converter.component.html',
  styleUrls: ['./currency-converter.component.css']
})
export class CurrencyConverterComponent {
  amount: Number = 0;
  result = '';

  constructor(private currencyConverterService: CurrencyConverterService) { }

  convertCurrency() {
    this.currencyConverterService.convertCurrency(this.amount).subscribe(result => {
      console.log(result); // Added for debugging
      this.result = result.currencyText;
    }, error => console.error(error));
  }
}
<div class="card" style="width: 18rem;">
  <div class="card-body">
    <p>currency-converter works!</p>
    <div>
      <input type="text" [(ngModel)]="amount" pattern="^\d{1,3}( \d{3})*,?\d{0,2}$" placeholder="Enter amount">
      <button (click)="convertCurrency()">Convert</button>
      <p class="result text-wrap" style="overflow-wrap: break-word;">{{result}}</p>
    </div>
  </div>
</div>
